import React, { Component } from "react";
import { connect } from "react-redux";
import Poll from "./Poll";

class PollDashboard extends Component {
  state = {
    answered: false,
  };

  handleAnswered = (e, answer) => {
    e.preventDefault();
    this.setState(() => ({
      answered: answer,
    }));
  };

  render() {
    return (
      <div className="center">
        <ul>
          <li>
            <button onClick={this.handleAnswered(false)}>
              Unanswered Questions
            </button>
          </li>
          <li>
            <button onClick={this.handleAnswered(true)}>
              Answered Questions
            </button>
          </li>
        </ul>

        <h1>
          {this.state.answers ? "Answered Questions" : "Unanswered Questions"}
        </h1>
        {this.state.answered === true
          ? this.props.questionIds.map(
              (id) => this.props.answered[id] != null && <Poll id={id} />
            )
          : this.props.questionIds.map(
              (id) => this.props.answered[id] == null && <Poll id={id} />
            )}
      </div>
    );
  }
}

function mapStateToProps({ authedUser, users, questions }) {
  return {
    authedUser,
    questionIds: Object.keys(questions).sort(
      (a, b) => questions[b].timestamp - questions[a].timestamp
    ),
    answered: users[authedUser].answers,
  };
}

export default connect(mapStateToProps)(PollDashboard);
