import React, { Component } from "react";
import { connect } from "react-redux";

class Leaderblock extends Component {
  render() {
    const { name, avatarURL, questions, answers } = this.props.user;
    const numAnsweredQuestions = Object.keys(answers).length;
    const numCreatedQuestions = Object.keys(questions).length;

    return (
      <div className="poll">
        <h3>{name} asks:</h3>
        <img src={avatarURL} alt="Author avatar" className="question-avatar" />
        <h4>Answered questions: {numAnsweredQuestions}</h4>
        <h4>Created questions: {numCreatedQuestions}</h4>
        <h4>Score</h4>
        <h3>{numAnsweredQuestions + numCreatedQuestions}</h3>
      </div>
    );
  }
}

function mapStateToProps({ authedUser, users }, { id }) {
  const user = users[id];

  return {
    user,
    authedUser,
  };
}

export default connect(mapStateToProps)(Leaderblock);
