import React, { Component } from "react";
import { connect } from "react-redux";

class Question extends Component {
  render() {
    const { question, questionAuthor, authedUser } = this.props;

    if (question === null) {
      return <p>This question doesn't exist</p>;
    }

    const { optionOne, optionTwo } = question;
    const { name, avatarURL } = questionAuthor;

    const answered =
      optionOne.votes.includes(authedUser) ||
      optionTwo.votes.includes(authedUser);

    const totalVotes = optionOne.votes.length + optionTwo.votes.length;

    return (
      <div>
        {!answered ? (
          <div>
            <h3>{name} asks:</h3>
            <img
              src={avatarURL}
              alt="Author avatar"
              className="question-avatar"
            />
            <h4>Would you rather...</h4>
            <form>
              <input type="radio" name="option" value={optionOne.text} />
              {optionOne.text}
              <br></br>
              <input type="radio" name="option" value={optionTwo.text} />
              {optionTwo.text}
              <br></br>
              <button type="button">Submit</button>
            </form>
          </div>
        ) : (
          <div>
            <h3>Asked by {name}:</h3>
            <img
              src={avatarURL}
              alt="Author avatar"
              className="question-avatar"
            />
            <h4>Results</h4>
            <div
              className={
                optionOne.votes.includes(authedUser) && "answered-vote"
              }
            >
              <p>Would you rather {optionOne.text}</p>
              <p>
                {optionOne.votes.length} of {totalVotes} votes (
                {(optionOne.votes.length / totalVotes) * 100}%)
              </p>
            </div>
            <div
              className={
                optionOne.votes.includes(authedUser) && "answered-vote"
              }
            >
              <p>Would you rather {optionTwo.text}</p>
              <p>
                {optionTwo.votes.length} of {totalVotes} votes (
                {(optionTwo.votes.length / totalVotes) * 100}%)
              </p>
            </div>
          </div>
        )}
      </div>
    );
  }
}

function mapStateToProps({ authedUser, users, questions }, { id }) {
  const question = questions[id];

  return {
    authedUser,
    question: question,
    questionAuthor: users[question.author],
  };
}

export default connect(mapStateToProps)(Question);
