import React, { Component } from "react";
import { connect } from "react-redux";
import { setAuthedUser } from "../actions/authedUser";
import { Paper, Typography } from "@material-ui/core";

class LoginPage extends Component {
  state = {
    authedUser: "",
  };

  handleLogin = (e) => {
    e.preventDefault();

    this.props.dispatch(setAuthedUser(this.state.authedUser));
  };

  handleChange = (e) => {
    this.setState({ authedUser: e.target.value });
  };

  render() {
    return (
      <div>
        <Paper elevation={2} className="new-question-container">
          <Typography variant="h1">
            Welcome to the Would you Rather App
          </Typography>
          <Typography variant="h3">Please sign in to continue</Typography>
        </Paper>

        <h2></h2>
        <form onSubmit={this.handleLogin}>
          <select value={this.state.authedUser} onChange={this.handleChange}>
            <option disabled="disabled" value={this.state.authedUser}>
              {this.state.authedUser}
            </option>
            {Object.keys(this.props.users).map((id) => (
              <option key={id} value={id}>
                {id}
              </option>
            ))}
          </select>
          <button type="submit">Sign in</button>
        </form>
      </div>
    );
  }
}

function mapStateToProps({ authedUser, users }) {
  return {
    authedUser,
    users,
  };
}

export default connect(mapStateToProps)(LoginPage);
