import React, { Component } from "react";
import { connect } from "react-redux";
import Leaderblock from "./Leaderblock";

class Leaderboard extends Component {
  render() {
    const { authedUser, orderedIds } = this.props;

    return (
      <div>
        <ul>
          {orderedIds.map((id) => (
            <li key={id}>
              <Leaderblock id={id} />
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
function mapStateToProps({ authedUser, users }) {
  const scores = Object.values(users).map((user) => ({
    [user.id]:
      Object.keys(user.answers).length + Object.keys(user.questions).length,
  }));

  return {
    authedUser,
    orderedIds: Object.keys(users).sort((a, b) => scores[b] - scores[a]),
  };
}

export default connect(mapStateToProps)(Leaderboard);
