import React, { Component } from "react";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";
import { Card, Typography, Avatar, Button } from "@material-ui/core";

class Poll extends Component {
  render() {
    const { id, question, questionAuthor } = this.props;

    const { optionOne } = question;
    const { name, avatarURL } = questionAuthor;

    return (
      <div className="poll">
        <h4>{`${name} asks`}</h4>
        <img src={avatarURL} alt="User avatar" />
        <h5>Would you rather...</h5>
        <p>{optionOne.text} or...</p>
        {/* <Card elevation={2}>
          <Typography variant="h4" component="h4">{`${name} asks`}</Typography>
          <Avatar src={avatarURL} />
          <Typography variant="h5" component="h5">
            Would you rather...
          </Typography>
          <Typography variant="p" component="body1">
            {optionOne.text} or...
          </Typography> */}
        <Link to={`/questions/${id}`}>
          <Button variant="contained" color="primary" component="span">
            View Poll
          </Button>
        </Link>
        {/* </Card> */}
      </div>
    );
  }
}

function mapStateToProps({ users, questions }, { id }) {
  const question = questions[id];

  return {
    question: question,
    questionAuthor: users[question.author],
  };
}

export default withRouter(connect(mapStateToProps)(Poll));
