import React, { Component, Fragment } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { connect } from "react-redux";
import { handleInitialData } from "../actions/shared";
import LoadingBar from "react-redux-loading";
import Nav from "./Nav";
import PollDashboard from "./PollDashboard";
import Leaderboard from "./Leaderboard";
import NewQuestion from "./NewQuestion";
import Question from "./Question";
import LoginPage from "./LoginPage";

class App extends Component {
  componentDidMount() {
    this.props.dispatch(handleInitialData("sarahedo"));
  }

  render() {
    return (
      <Router>
        <Fragment>
          <LoadingBar />
          <div className="App">
            {this.props.loading === true ? null : this.props.authedUser ===
              "" ? (
              <LoginPage />
            ) : (
              <div>
                <Nav authedUser={this.props.authedUserAvatar} />
                <Route exact path="/" component={PollDashboard} />
                <Route path="/add" component={NewQuestion} />
                <Route path="/leaderboard" component={Leaderboard} />
                <Route path="/questions/:question_id" component={Question} />
              </div>
            )}
          </div>
        </Fragment>
      </Router>
    );
  }
}

function mapStateToProps({ authedUser, users }) {
  return {
    loading: authedUser === null,
    authedUser,
    authedUserAvatar: users[authedUser] ? "" : users[authedUser].avatarURL,
  };
}

export default connect(mapStateToProps)(App);
