import React, { Component } from "react";
import { connect } from "react-redux";

class Question extends Component {
  render() {
    const { question, questionAuthor } = this.props;

    if (question === null) {
      return <p>This question doesn't exist</p>;
    }

    const { optionOne, optionTwo } = question;
    const { name, avatarURL } = questionAuthor;

    return (
      <div>
        <h3>{name} asks:</h3>
        <img src={avatarURL} alt="Author avatar" className="question-avatar" />
        <h4>Would you rather...</h4>
        <select name="options">
          <option value={optionOne.text}>{optionOne.text}</option>
          <option value={optionTwo.text}>{optionTwo.text}</option>
        </select>
      </div>
    );
  }
}

function mapStateToProps({ authedUser, users, questions }, { id }) {
  const question = questions[id];

  return {
    authedUser,
    question: question,
    questionAuthor: users[question.author],
  };
}

export default connect(mapStateToProps)(Question);
