import React, { Component } from "react";
import { connect } from "react-redux";
import { handleAddQuestionAnswer } from "../actions/shared";
import { Paper, Typography, Divider, Grid } from "@material-ui/core";

class Question extends Component {
  state = {
    selectedOption: null,
  };

  handleOptionChange = (e) => {
    this.setState({
      selectedOption: e.target.value,
    });
  };

  handleAnswer = (e) => {
    e.preventDefault();

    const { dispatch, authedUser } = this.props;
    const { question_id } = this.props.match.params;

    dispatch(
      handleAddQuestionAnswer({
        qid: question_id,
        authedUser,
        answer: this.state.selectedOption,
      })
    );
  };

  render() {
    const { question, questionAuthor, authedUser } = this.props;

    //Handling question id not found
    if (Object.keys(question).length === 0) {
      return <p>This question doesn't exist</p>;
    }

    const { optionOne, optionTwo } = question;
    const { name, avatarURL } = questionAuthor;

    const answered =
      optionOne.votes.includes(authedUser) ||
      optionTwo.votes.includes(authedUser);

    const totalVotes = optionOne.votes.length + optionTwo.votes.length;

    return (
      <div>
        <Paper elevation={2} className="new-question-container">
          <Typography variant="h6" component="h4">
            {!answered ? `${name} asks` : `Asked by ${name}`}
          </Typography>
          <Divider light />
          <Grid
            container
            direction="row"
            alignContent="center"
            justify="space-around"
          >
            <Grid item className="poll-info-container">
              <img
                src={avatarURL}
                alt="User avatar"
                className="question-avatar"
              />
            </Grid>
            <Grid item>
              <Typography variant="h6">
                {!answered ? "Would you rather" : "Results"}
              </Typography>
              {!answered ? (
                <form onSubmit={this.handleAnswer}>
                  <input
                    type="radio"
                    value="optionOne"
                    checked={this.state.selectedOption === "optionOne"}
                    onChange={this.handleOptionChange}
                  />
                  {optionOne.text}
                  <br></br>
                  <input
                    type="radio"
                    value="optionTwo"
                    checked={this.state.selectedOption === "optionTwo"}
                    onChange={this.handleOptionChange}
                  />
                  {optionTwo.text}
                  <br></br>
                  <button
                    type="submit"
                    disabled={this.state.selectedOption === null}
                  >
                    Submit
                  </button>
                </form>
              ) : (
                <div></div>
              )}
            </Grid>
          </Grid>
        </Paper>
        {!answered ? (
          <div>
            <h3>{name} asks:</h3>
            <img
              src={avatarURL}
              alt="Author avatar"
              className="question-avatar"
            />
            <h4>Would you rather...</h4>
          </div>
        ) : (
          <div>
            <h3>Asked by {name}:</h3>
            <img
              src={avatarURL}
              alt="Author avatar"
              className="question-avatar"
            />
            <h4>Results</h4>
            <div
              className={
                optionOne.votes.includes(authedUser)
                  ? "answered-vote"
                  : "unanswered-vote"
              }
            >
              <p>Would you rather {optionOne.text}</p>
              <p>
                {optionOne.votes.length} of {totalVotes} votes (
                {(optionOne.votes.length / totalVotes) * 100}%)
              </p>
            </div>
            <div
              className={
                optionTwo.votes.includes(authedUser)
                  ? "answered-vote"
                  : "unanswered-vote"
              }
            >
              <p>Would you rather {optionTwo.text}</p>
              <p>
                {optionTwo.votes.length} of {totalVotes} votes (
                {(optionTwo.votes.length / totalVotes) * 100}%)
              </p>
            </div>
          </div>
        )}
      </div>
    );
  }
}

function mapStateToProps({ authedUser, users, questions }, props) {
  const { question_id } = props.match.params;

  return {
    id: question_id,
    authedUser,
    question: !questions[question_id] ? {} : questions[question_id],
    questionAuthor: !questions[question_id]
      ? {}
      : users[questions[question_id].author],
  };
}

export default connect(mapStateToProps)(Question);
